{
  lastPopupButton = jQuery(elem);

  return lastPopupButton.hasClass('disabled')
    ? false
    : loadDialogByLink(
      elem,
      URLHandler.buildURL(this.getURLParams(elem)),
      this.options,
      this.callback,
      this
    );
};

